import React, { useState } from "react";
import "./Square.css";
import "./Board.css";

function Square(props: any) {
  return (
    <div className="square" onClick={props.onClick}>
      {props.ch}
    </div>
  );
}

interface Checker {
  colour: string;
  x: number;
  y: number;
  hasPromoted: boolean;
}

export function Board() {
  const [board, setBoard] = useState([
    [
      null,
      { colour: "Black", x: 1, y: 0, hasPromoted: false },
      null,
      { colour: "Black", x: 3, y: 0, hasPromoted: false },
      null,
      { colour: "Black", x: 5, y: 0, hasPromoted: false },
      null,
      { colour: "Black", x: 7, y: 0, hasPromoted: false },
    ],
    [
      { colour: "Black", x: 0, y: 1, hasPromoted: false },
      null,
      { colour: "Black", x: 2, y: 1, hasPromoted: false },
      null,
      { colour: "Black", x: 4, y: 1, hasPromoted: false },
      null,
      { colour: "Black", x: 6, y: 1, hasPromoted: false },
      null,
    ],
    [
      null,
      { colour: "Black", x: 1, y: 2, hasPromoted: false },
      null,
      { colour: "Black", x: 3, y: 2, hasPromoted: false },
      null,
      { colour: "Black", x: 5, y: 2, hasPromoted: false },
      null,
      { colour: "Black", x: 7, y: 2, hasPromoted: false },
    ],
    [null, null, null, null, null, null, null, null],
    [null, null, null, null, null, null, null, null],
    [
      { colour: "Red", x: 0, y: 5, hasPromoted: false },
      null,
      { colour: "Red", x: 2, y: 5, hasPromoted: false },
      null,
      { colour: "Red", x: 4, y: 5, hasPromoted: false },
      null,
      { colour: "Red", x: 6, y: 5, hasPromoted: false },
      null,
    ],
    [
      null,
      { colour: "Red", x: 1, y: 6, hasPromoted: false },
      null,
      { colour: "Red", x: 3, y: 6, hasPromoted: false },
      null,
      { colour: "Red", x: 5, y: 6, hasPromoted: false },
      null,
      { colour: "Red", x: 7, y: 6, hasPromoted: false },
    ],
    [
      { colour: "Red", x: 0, y: 7, hasPromoted: false },
      null,
      { colour: "Red", x: 2, y: 7, hasPromoted: false },
      null,
      { colour: "Red", x: 4, y: 7, hasPromoted: false },
      null,
      { colour: "Red", x: 6, y: 7, hasPromoted: false },
      null,
    ],
  ]);

  function isValidCoordinate(coordinate: number[]) {
    return (
      coordinate[0] >= 0 &&
      coordinate[0] < 8 &&
      coordinate[1] >= 0 &&
      coordinate[1] < 8
    );
  }

  function getMoves(x: number, y: number) {
    return getNonCaptureMoves(x, y);
  }

  function getNonCaptureMoves(x: number, y: number) {
    let moves: number[][];
    let directionFactor: number = board[y][x]!.colour === "Red" ? -1 : 1;
    //get possible move locations
    if (board[y][x]!.hasPromoted)
      moves = [
        [x + 1, y + 1],
        [x - 1, y + 1],
        [x + 1, y - 1],
        [x - 1, y - 1],
      ];
    else
      moves = [
        [x + 1, y + directionFactor],
        [x - 1, y + directionFactor],
      ];

    let filteredMoves: number[][] = [];
    for (let i = 0; i < moves.length; i++) {
      if (
