import React, { useState } from "react";
import "./Square.css";
import "./Board.css";
import "./checkersState";
import {
  Colour,
  TurnType,
  Turn,
  Board,
  Position,
  Checker,
  newState,
  CheckersState,
  isValidCoordinate,
  isValidPiece,
} from "./checkersState";

function Square(props: any) {
  return (
    <div className="square" onClick={props.onClick}>
      {props.ch}
    </div>
  );
}

export function CheckerBoard() {
  const [checkersState, setCheckersState] = useState({
    gameState: newState(),
    currentPieceClicked: { x: -1, y: -1 },
  });

  console.log("Render!");
  let bigboard = [];
  for (let i = 0; i < 8; i++) {
    let temp = [];
    for (let j = 0; j < 8; j++) {
      let square: Checker | null = checkersState.gameState.board.grid[i][j];
      let chr: string | null = null;
      if (square !== null) {
        let col: string = square!.colour === Colour.Red ? "r" : "b";
        chr = square!.hasPromoted ? col.toUpperCase() : col;
      }
      let val: string = String(i) + String(j);
      temp.push(<Square key={val} ch={chr} onClick={() => console.log(chr)} />);
    }
    bigboard.push(
      <div key={i} className="row">
        {temp}
      </div>
    );
  }
  return <div className="bigboard">{bigboard}</div>;
}
